    reg [6:1] ledr_inner, ledg_inner;

    always @(posedge gtx0_rxusrclk2_i)

    begin
        if (gtx0_rxcharisk_i == 2'b01 ) begin
            if (gtx0_rxdata_i == 16'h503C) begin
