            end else begin
                  rxgood0 <= 2'b1;
            end
        end
        if (gtx1_rxcharisk_i == 2'b01) begin
            if (gtx1_rxdata_i == 16'h50BC) begin
                  rxgood1 <= 2'b0;
            end else begin
                  rxgood1 <= 2'b1;
            end
