    (
        .rst(gtx1_rx_system_reset_c),
        .wr_clk(gtx1_rxusrclk2_i),
        .rd_clk(rx_vio_ila_clk_i),
        .din(gtx1_rx_data_to_fifo_i),  
        .wr_en(1'b1),
        .rd_en(1'b1),
        .dout(gtx1_rx_data_from_fifo_i),  
        .full(),
        .empty()
    );

    assign gtx1_rx_data_fifo_vio_async_in_i = gtx1_rx_data_from_fifo_i[148:117];

    assign gtx1_rx_data_fifo_vio_sync_in_i = gtx1_rx_data_from_fifo_i[116:85];

    assign gtx1_fifo_ila_in_i = gtx1_rx_data_from_fifo_i[84:0];


    // assign resets for frame_gen modules
    assign  gtx0_tx_system_reset_c = !gtx0_txresetdone_r2 || user_tx_reset_i;
