    assign  gtx0_rxeqmix_i                       =  rx_data_vio_async_out_i[27:25];
    assign  gtx0_ila_in_i[84:83]                 =  gtx0_rxchariscomma_i;
    assign  gtx0_ila_in_i[82:81]                 =  gtx0_rxcharisk_i;
    assign  gtx0_ila_in_i[80:79]                 =  gtx0_rxdisperr_i;
