    assign  tx_data_vio_sync_in_i =     (mux_sel_i == 1'b0)?gtx0_tx_data_vio_sync_in_i:
                                                            gtx1_tx_data_vio_sync_in_i;

    assign  rx_data_vio_async_in_i =    (mux_sel_i == 1'b0)?gtx0_rx_data_fifo_vio_async_in_i:
                                                            gtx1_rx_data_fifo_vio_async_in_i;


    assign  rx_data_vio_sync_in_i =     (mux_sel_i == 1'b0)?gtx0_rx_data_fifo_vio_sync_in_i:
                                                            gtx1_rx_data_fifo_vio_sync_in_i;


    assign  ila_in_i =                  (mux_sel_i == 1'b0)?gtx0_fifo_ila_in_i:
                                                            gtx1_fifo_ila_in_i;

